import ast
from tecnam import TecnamP92
from collections.abc import Iterable
import inspect
import math

def test_equal(x, y) :
    if isinstance(x, float) and isinstance(y, float) :
        if math.isnan(x) :
            return math.isnan(y)
        else :
            return math.isclose(x, y, rel_tol = 1e-07, abs_tol = 1e-15)
    elif isinstance(x, Iterable) and isinstance(y, Iterable) :
        if len(x) != len(y) :
            return False
        for i in range(len(x)) :
            if not test_equal(x[i], y[i]) :
                return False
        return True
    elif isinstance(x, TecnamP92) and isinstance(y, TecnamP92):
        return compare_aircraft(x, y)        
    else :
        return (x == y) 

def compare_aircraft(obj1, obj2):
    # Get the attribute names of both objects
    attrs1 = dir(obj1)
    attrs2 = dir(obj2)

    # Remove special methods (methods starting with '__')
    attrs1 = [attr for attr in attrs1 if not attr.startswith('__')]
    attrs2 = [attr for attr in attrs2 if not attr.startswith('__')]

    # If the objects don't have the same attributes, return False
    if set(attrs1) != set(attrs2):
        return False

    # Check that the values of each attribute are the same
    for attr in attrs1:
        if not test_equal(getattr(obj1, attr), getattr(obj2, attr)):
            return False

    # If all attributes match, return True
    return True

